layersTree.prototype.getLayerVisibility = function(box)
{
	if (!box.checked)
		return false;

	var	el = box.parentNode.parentNode.parentNode;

	while (!el.root)
	{
		var group = $(el).children("[GroupID]");

		if (group.length > 0)
		{
			if (!group[0].firstChild.checked)
				return false;
		}

		el = el.parentNode;
	}

	return true;
}

