			                ttFrame.show().animate({x: newcoord.x, y: newcoord.y, width:newWidth}, 200 * isLabelVisible);
			                ttText.attr({text: String(barValues[j].value)}).show().animate({x: newcoord.x + 6, y: newcoord.y + 9}, 200 * isLabelVisible);
